import React from 'react';
import { auth, googleProvider, facebookProvider } from './firebase';
import { signInWithPopup } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';
import { FaGoogle, FaFacebook, FaGamepad, FaSteam } from 'react-icons/fa';

export default function Signup() {
  const navigate = useNavigate();

  const handleSocialLogin = (provider) => {
    signInWithPopup(auth, provider)
      .then(() => {
        navigate('/dashboard');
      })
      .catch((err) => {
        console.error(err);
        alert('Errore durante il login.');
      });
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center px-4">
      <h1 className="text-3xl font-bold mb-4">Registrati con un click</h1>
      <p className="mb-6 text-center text-gray-300">
        Scegli il tuo account preferito per entrare su LootClub
      </p>
      <div className="flex gap-6 text-4xl">
        <button onClick={() => handleSocialLogin(googleProvider)} className="hover:scale-110 transition">
          <FaGoogle className="text-red-400" />
        </button>
        <button onClick={() => handleSocialLogin(facebookProvider)} className="hover:scale-110 transition">
          <FaFacebook className="text-blue-500" />
        </button>
        <button disabled className="cursor-not-allowed text-purple-400" title="Presto disponibile">
          <FaGamepad />
        </button>
        <button disabled className="cursor-not-allowed text-gray-400" title="Presto disponibile">
          <FaSteam />
        </button>
      </div>
    </div>
  );
}
